// counter 
    var intervaltiming = setInterval(function(){
        var myclassname = document.getElementsByClassName("four-element-carousel-counter");
        
        for (i = 0; i < myclassname.length; i++) {
                var getcounterid = myclassname[i].getAttribute("id");
                var getcarouselparent = document.getElementById(getcounterid).getAttribute('data-carousel-parent').slice(1,);
                var getcarousel = document.getElementById(getcounterid).getAttribute('data-carousel-target').slice(1,);

                var slidelength = document.getElementById(getcarouselparent).getElementsByClassName(getcarousel)[0].getElementsByClassName("slick-slide").length;

                var currentslide = " ";
                var slickslide = document.getElementById(getcarouselparent).getElementsByClassName("slick-slide");
                var c;
                for (c = 0; c < slickslide.length; c++) {
                    if(slickslide[c].classList.contains("slick-current") == true){
                        currentslide = Number(c) + 1
                    }
                }
                document.getElementById(getcounterid).getElementsByClassName("current-item")[0].innerText = "0" + currentslide;
                document.getElementById(getcounterid).getElementsByClassName("total-item")[0].innerText = "0" + slidelength;
        }

    }, 100);

